function doStuff($data)
{   
    $letter_count = Array();
    $final_count = [];
    $data = explode(",",$data);
    $iteration = 0;
    $max_matches = 0;
    foreach ($data as $item) {
        $unique_array = Array();
        $item_arr = str_split($item);
        sort($item_arr);
        foreach ($item_arr as $l) {
            if(in_array($l,$unique_array) == false){
                array_push($unique_array,$l);
                $letter_count[$iteration][$l] = 1;
            }
            else{
                $letter_count[$iteration][$l] += 1;
                if($letter_count[$iteration][$l] > $max_matches){
                    $max_matches = $letter_count[$iteration][$l];
                }
            }
        }

        $iteration +=1;
    }
    foreach ($letter_count as $item){
        $tmp = [];
        foreach($item as $count){
          if(in_array($count,$tmp)==false){
              if(array_key_exists($count,$final_count)){
                $final_count[$count] += 1;
              }else{
                $final_count[$count] = 1;
              }
              array_push($tmp,$count);
          }
        }
    }
    if(array_key_exists("1",$final_count)){
        array_shift($final_count);
    }
    return array_product($final_count);
}

