function doStuff($data)
{
    $data = explode(",", $data);
    $iteration = 0;
    $matches = [];
    $match_ratio = 0;
    $total_matches = 0;
    foreach ($data as $items) {
        $item_arr = str_split($items);
        $iteration += 1;
        for ($i = $iteration; $i < count($data); $i++) {
            $match_this = str_split($data[$i]);
            $pos_match = 0;
            $length = count($match_this);
            for ($k = 0; $k < count($item_arr); $k++) {
                if ($item_arr[$k] == $match_this[$k]) {
                    $pos_match += 1;
                }
            }
            $tmp_match_ratio = ($pos_match / $length) * 100;

            if ($tmp_match_ratio > $match_ratio) {
                $old_ratio = $match_ratio;
                $match_ratio = $tmp_match_ratio;
                $matches = [$items, $data[$i]];
                echo ("Old MR: " . $old_ratio . " New MR: " . $match_ratio . " Matches: " . $matches[0] . ", " . $matches[1] . "</br>");
            }
        }
    }
    $match_result = common_letters($matches);
    echo("\nCommon Letters: <strong>".$match_result."</strong>\n");
}
function common_letters($input)
{
    $tmp1 = str_split($input[0]);
    $tmp2 = str_split($input[1]);
    $result = "";
    for ($i = 0; $i < count($tmp1); $i++) {

        if ($tmp1[$i] == $tmp2[$i]) {
            $result .= $tmp1[$i];
        }
    }
    return $result;
}
